sys_script_client.script:
var originalListItems = [];
var changed = false;
var retrieveElements = g_form.getControl("u_what_needed");
var whatNeededRelatedChoices = [];

function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
		return;
	}
// 		Store the original Elements after retrival in only if they were never another item selected before.
	if (!changed) {
		for (var index = 0; index < retrieveElements.length; index++){
			originalListItems.push(retrieveElements[index]);
		}
	}
	updateDropDown(newValue);
}

function updateDropDown(newValue){
	for (var index = 0; index < originalListItems.length; index++){
		if ( originalListItems[index].value.includes(newValue)){
			whatNeededRelatedChoices.push(originalListItems[index]);
		}
	}
	g_form.clearOptions("u_what_needed");

	for (var i = 0; i < whatNeededRelatedChoices.length; i++) {
		g_form.addOption(
			"u_what_needed",
			String(whatNeededRelatedChoices[i].value),
			String(whatNeededRelatedChoices[i].innerText)
		);
	}
	whatNeededRelatedChoices = [];
}
